CREATE TABLE "badges" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "recipient_id" INTEGER NOT NULL , "issuedOn" DATETIME NOT NULL  DEFAULT CURRENT_TIMESTAMP, "uid" VARCHAR NOT NULL UNIQUE, "badge_img" BLOB);
CREATE TABLE "participant" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "created_on" DATETIME NOT NULL  DEFAULT CURRENT_TIMESTAMP, "identity_hash" VARCHAR NOT NULL , "email" VARCHAR NOT NULL, "pwd_hash" VARCHAR NOT NULL);
CREATE TABLE "slide_results" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "slide_id" INTEGER NOT NULL , "participant_id" INTEGER NOT NULL , "created_on" DATETIME NOT NULL  DEFAULT CURRENT_TIMESTAMP, "q1" INTEGER, "q2" INTEGER, "q3" INTEGER);
CREATE TABLE "slides" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "label" VARCHAR NOT NULL, "name" VARCHAR NOT NULL , "created_on" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP);
