CREATE TABLE "badges" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "recipient_id" INTEGER NOT NULL , "issuedOn" DATETIME NOT NULL  DEFAULT CURRENT_TIMESTAMP, "uid" VARCHAR NOT NULL );
CREATE TABLE "recipients" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "created_on" DATETIME NOT NULL  DEFAULT CURRENT_TIMESTAMP, "identity_hash" VARCHAR NOT NULL , "email" VARCHAR NOT NULL );
CREATE TABLE "slide_results" ("id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL , "slide_id" INTEGER NOT NULL , "student_id" INTEGER NOT NULL , "created_on" DATETIME NOT NULL  DEFAULT CURRENT_TIMESTAMP, "q1" INTEGER, "q2" INTEGER, "q3" INTEGER);
